import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';
import { Terminal as XTerm } from 'xterm';
import { FitAddon } from 'xterm-addon-fit';
import 'xterm/css/xterm.css';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 100%;
`;

const Header = styled.div`
  padding: 10px;
  background-color: #f5f5f5;
  font-weight: bold;
  border-bottom: 1px solid #ccc;
`;

const TerminalContainer = styled.div`
  flex: 1;
  background-color: #1e1e1e;
  overflow: hidden;
`;

const Terminal = () => {
  const terminalRef = useRef(null);
  const xtermRef = useRef(null);
  const fitAddonRef = useRef(null);

  useEffect(() => {
    // Initialize xterm.js
    xtermRef.current = new XTerm({
      cursorBlink: true,
      theme: {
        background: '#1e1e1e',
        foreground: '#f0f0f0',
      },
      fontSize: 14,
      fontFamily: 'Menlo, Monaco, "Courier New", monospace',
    });

    fitAddonRef.current = new FitAddon();
    xtermRef.current.loadAddon(fitAddonRef.current);

    if (terminalRef.current) {
      xtermRef.current.open(terminalRef.current);
      fitAddonRef.current.fit();

      // Welcome message
      xtermRef.current.writeln('Welcome to the Terminal!');
      xtermRef.current.writeln('This is a simulated terminal for demonstration purposes.');
      xtermRef.current.writeln('In a real implementation, this would connect to a backend service.');
      xtermRef.current.writeln('');
      xtermRef.current.write('$ ');

      // Handle user input
      xtermRef.current.onData(data => {
        // Echo user input
        xtermRef.current.write(data);

        // If user presses Enter, process the command
        if (data === '\r') {
          xtermRef.current.writeln('');
          xtermRef.current.writeln('Command not implemented in this demo.');
          xtermRef.current.write('$ ');
        }
      });
    }

    // Handle resize
    const handleResize = () => {
      if (fitAddonRef.current) {
        fitAddonRef.current.fit();
      }
    };

    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      window.removeEventListener('resize', handleResize);
      if (xtermRef.current) {
        xtermRef.current.dispose();
      }
    };
  }, []);

  return (
    <Container>
      <Header>Terminal</Header>
      <TerminalContainer ref={terminalRef} />
    </Container>
  );
};

export default Terminal;
